import React from "react"
import { Link } from "react-router-dom"


class Developer extends React.Component {
    /*
                Email: {this.props.developer.email}
                Phone: {this.props.developer.phone}
                Location: {this.props.developer.location}
                Price per hour: {this.props.developer.pricePerHour}
                Technology: {this.props.developer.technology}
                Years of expiriance: {this.props.developer.yearsOfExp}
                Native language: {this.props.developer.nativeLang}

    */
    hideMenu() {
        document.getElementById("menu").style.display = "none";
    }

    render() {
        return (
            <div>
                <li className="developer-container card">
                    <div className="developer-left">
                        <div className="profile-picture">
                            <img src={this.props.developer.profilePicture ?
                                this.props.developer.profilePicture :
                                "data:image/png;base64,"}
                                alt="profile-picture" />
                        </div>
                        <div className="developer-options">
                            <button onClick={() => this.props.deleteDeveloperProps(this.props.developer.id)} className="btn btn-delete">Delete</button>
                            <Link to={`/update/${this.props.developer.id}`} className="btn btn-update" onClick={this.hideMenu}>Update</Link>
                            <div className="hire-developer">
                                {this.props.developer.hiredFrom ?
                                    <p>Hired from {this.props.developer.hiredFrom} to {this.props.developer.hiredTo}</p>
                                    :
                                    <div>
                                        <label>Hire: </label>
                                        <input type="checkbox" checked={this.props.developer.checked} onChange={() => this.props.handleChangeProps(this.props.developer.id)} />
                                    </div>
                                }
                            </div>
                        </div>
                    </div>
                    <div className="developer-right">
                        <div>
                            <p>Name: {this.props.developer.name}</p>
                            <p>Email: {this.props.developer.email}</p>
                            <p>Phone: {this.props.developer.phone}</p>
                            <p>Location: {this.props.developer.location}</p>
                            <p>Native language: {this.props.developer.nativeLang}</p>
                        </div>
                        <div>
                            <p>Price per hour: ${this.props.developer.pricePerHour}</p>
                            <p>Technology: {this.props.developer.technology}</p>
                            <p>Years of expiriance: {this.props.developer.yearsOfExp}</p>
                            <p><a href={this.props.developer.linkedInLink}>LinkedIn</a></p>
                            <p class="wrap-text">Description: {this.props.developer.description}</p>
                        </div>
                    </div>
                </li>
            </div>
        )

    }
}

export default Developer